        .push(0xf0 | codePoint >>> 18 & 0x7)
        .push(0x80 | codePoint >>> 12 & 0x3f)
        .push(0x80 | codePoint >>> 6  & 0x3f)
        .push(0x80 | codePoint & 0x3f);

      i += 1;
      nextCodePoint = string.charCodeAt(i + 1);
    } else {
      bytes
        .push(0xe0 | codePoint >>> 12)
        .push(0x80 | codePoint >>> 6 & 0x3f)
        .push(0x80 | codePoint & 0x3f);
