  GUID: 1,

  // Parallel arrays
  callbacks: ([] : Array<?Function>),
  types: ([] : Array<?JSTimerType>),
  timerIDs: ([] : Array<?number>),
  immediates: [],
  requestIdleCallbacks: [],
